sys_variable_value.value:
(function execute(inputs, outputs) {
  var oldString = inputs.original_comma_separated_string.toString();
  var stringToAdd = inputs.string_to_add.toString();

  var newString = !gs.nil(oldString) ? oldString + ',' + stringToAdd : stringToAdd;

  outputs.new_string = newString;
})(inputs, outputs);

